mapper.select:
SELECT A.PROD_ID, A.PROD_PRICE, A.PROD_NAME, A.PROD_GRADE, B.FILE_NAME FROM PRODUCTS A, PROD_UPLOAD_IMAGES B WHERE FILE_MAIN= 'Y' AND A.PROD_ID = B.PROD_ID AND PROD_YN = 'Y'
AND PROD_NAME LIKE '%'||#{prod_name}||'%'
AND PROD_CATEGORY LIKE '%'||#{prod_category}||'%'
AND PROD_GRADE = #{prod_grade}
AND PROD_PRICE BETWEEN #{prod_priceMin} AND #{prod_priceMax}

SELECT COUNT(*) FROM ( SELECT A.PROD_ID, A.PROD_PRICE, A.PROD_NAME, A.PROD_GRADE, B.FILE_NAME FROM PRODUCTS A, PROD_UPLOAD_IMAGES B WHERE FILE_MAIN= 'Y' AND A.PROD_ID = B.PROD_ID AND PROD_YN = 'Y'
AND PROD_NAME LIKE '%'||#{prod_name}||'%'
AND PROD_CATEGORY LIKE '%'||#{prod_category}||'%'
AND PROD_GRADE = #{prod_grade}
AND PROD_PRICE BETWEEN #{prod_priceMin} AND #{prod_priceMax}
)

SELECT A.PROD_ID , A.PROD_PRICE , A.PROD_NAME , A.PROD_GRADE , A.CREATED_AT , A.PROD_YN , B.FILE_NAME FROM PRODUCTS A, PROD_UPLOAD_IMAGES B WHERE FILE_MAIN= 'Y' AND A.PROD_ID = B.PROD_ID
AND PROD_NAME LIKE '%'||#{prod_name}||'%'
AND PROD_CATEGORY LIKE '%'||#{prod_category}||'%'
AND PROD_YN = #{prod_yn}
AND PROD_GRADE = #{prod_grade}
AND PROD_PRICE BETWEEN #{prod_priceMin} AND #{prod_priceMax}

SELECT COUNT(*) FROM ( SELECT A.PROD_ID , A.PROD_PRICE , A.PROD_NAME , A.PROD_GRADE , A.CREATED_AT , A.PROD_YN , B.FILE_NAME FROM PRODUCTS A, PROD_UPLOAD_IMAGES B WHERE FILE_MAIN= 'Y' AND A.PROD_ID = B.PROD_ID
AND PROD_NAME LIKE '%'||#{prod_name}||'%'
AND PROD_CATEGORY LIKE '%'||#{prod_category}||'%'
AND PROD_YN = #{prod_yn}
AND PROD_GRADE = #{prod_grade}
AND PROD_PRICE BETWEEN #{prod_priceMin} AND #{prod_priceMax}
)

mapper.update:
UPDATE PRODUCTS
SET PROD_NAME = #{prod_name}
	, PROD_PRICE = #{prod_price}
	, PROD_GRADE = #{prod_grade}

	, PROD_YN = 'Y'


	, PROD_YN = 'N'

WHERE PROD_ID = #{prod_id}

UPDATE PRODUCTS
SET PROD_YN = 'N'
WHERE PROD_ID = #{prod_id}
